import React from "react";

const plans = [
  {
    name: "Esencial",
    price: "CLP $24.995 / mes",
    discount: "50% OFF",
    description: "Tu primer paso hacia un soporte confiable.",
    features: [
      "1 usuario / 1 equipo",
      "Configuración y software esencial",
      "Preventivo mensual: antivirus y actualizaciones",
      "Respaldo local básico",
    ],
    highlight: false,
  },
  {
    name: "Avanza",
    price: "CLP $49.995 / mes",
    discount: "50% OFF",
    description: "El impulso que tu negocio necesita para crecer seguro.",
    features: [
      "Hasta 3 usuarios / equipos",
      "Soporte remoto esencial y avanzado",
      "Continuidad operativa y mantenimiento",
      "Asesoría remota en configuración",
    ],
    highlight: false,
  },
  {
    name: "Premium",
    price: "CLP $229.950 / mes",
    discount: "50% OFF",
    description: "Soporte integral con presencia en terreno cuando lo requieras.",
    features: [
      "Soporte remoto ilimitado",
      "Mantenimiento correctivo",
      "1 visita presencial mensual incluida",
      "Gestión de licencias corporativas",
    ],
    highlight: true,
  },
  {
    name: "Infinity",
    price: "Cotización / según alcance",
    discount: "",
    description: "Un plan sin límites, 100% a medida.",
    features: [
      "Cobertura ajustada a la empresa",
      "Soporte remoto y presencial a convenir",
      "Técnico residente opcional",
      "Servicios extra: licencias, monitoreo, web, TI",
    ],
    highlight: false,
  },
];

export default function Plans() {
  return (
    <section className="py-20 bg-gradient-to-b from-[#F8FAFB] to-[#E6EEF2]">
      <div className="text-center mb-12">
        <h2 className="text-4xl font-bold text-[#073956] mb-3">
          Nuestros Planes de Suscripción
        </h2>
        <p className="text-[#4B5563] max-w-2xl mx-auto">
          Elige el plan ideal para tu empresa. Diseñados para ofrecer soporte TI confiable y ciberseguridad profesional.
        </p>
      </div>

      <div className="flex flex-wrap justify-center gap-10 px-6">
        {plans.map((plan) => (
          <div
            key={plan.name}
            className={`relative w-full sm:w-80 rounded-2xl border transition-all duration-300 transform hover:-translate-y-1 shadow-md hover:shadow-xl ${
              plan.highlight
                ? "bg-gradient-to-b from-[#0EA5E9] to-[#073956] text-white border-[#073956]"
                : "bg-white border-[#D1E3E8]"
            }`}
          >
            {/* Descuento */}
            {plan.discount && !plan.highlight && (
              <div className="absolute top-4 right-4 bg-[#073956] text-white text-xs font-bold py-1 px-3 rounded-full shadow">
                {plan.discount}
              </div>
            )}

            {/* Etiqueta destacado */}
            {plan.highlight && (
              <div className="absolute -top-3 left-1/2 transform -translate-x-1/2 bg-white text-[#073956] text-xs px-4 py-1 rounded-full shadow font-semibold">
                RECOMENDADO
              </div>
            )}

            <div className="p-6 text-center">
              <h3
                className={`text-2xl font-semibold mb-2 ${
                  plan.highlight ? "text-white" : "text-[#1A3C47]"
                }`}
              >
                {plan.name}
              </h3>

              <p
                className={`italic mb-4 ${
                  plan.highlight ? "text-blue-100" : "text-[#4B5563]"
                }`}
              >
                {plan.description}
              </p>

              <p
                className={`text-2xl font-bold mb-6 ${
                  plan.highlight ? "text-white" : "text-[#073956]"
                }`}
              >
                {plan.price}
              </p>

              <ul
                className={`text-left mb-6 space-y-2 ${
                  plan.highlight ? "text-blue-50" : "text-[#4B5563]"
                }`}
              >
                {plan.features.map((feature, index) => (
                  <li key={index} className="flex items-center gap-2">
                    <span
                      className={`${
                        plan.highlight ? "text-white" : "text-[#0EA5E9]"
                      }`}
                    >
                      ✔
                    </span>
                    {feature}
                  </li>
                ))}
              </ul>

              <button
                className={`w-full py-2 rounded-lg font-semibold transition ${
                  plan.highlight
                    ? "bg-white text-[#073956] hover:bg-blue-100"
                    : "bg-[#073956] text-white hover:bg-[#0EA5E9]"
                }`}
              >
                {plan.name === "Infinity" ? "Contactar" : "Contratar"}
              </button>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
